import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from "react-router-dom";
import '@fortawesome/fontawesome-free/js/all';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Error from "./Error.js";
import Spinner from './Spinner.js';
import "./index.css";

function ResultDetails(props) {
  const location = useLocation();

  const [details, setDetails] = useState({});
  if (location.state !== null && location.state !== undefined) {
    setDetails(location.state.item_details);
  } else {
    fetchItems();
  }
  const [error, setError] = useState({message: ""});
  const [contributors, setContributors] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (location.state === null || location.state === undefined) {
      // retrieve item_details from fetch api call to search repository
      fetchItems();
    } else {
      setDetails(location.state.item_details);
    }
  }, []);

  const fetchItems = async () => {
    const response = await fetch("https://api.github.com" + location.pathname);
    const data = await response.json();

    if (response.status >= 400 || response.ok === false) {
      setError({message: data.message});
      const dataMsg = data.message;
      let errorMsg;
      try {
        errorMsg = data.errors[0].message;
      } catch (e) {
        errorMsg = false;
      }
      (errorMsg) ? setError({message: errorMsg}) : setError({message: dataMsg});
    } else {
      //traverse through list and find element that matches with id in location.pathname
      console.log(location.pathname)
      console.log(data);

      for (let i = 0; i < data.length; i++) {

      }

      setError({message: ""});

    }
  }

  useEffect(() => {
    fetchContributors();
  }, [details])

  const fetchContributors = async () => {
    const response = await fetch(details.contributors_url);
    const data = await response.json();

    if (response.status >= 400 || response.ok === false) {
      setError({message: data.message});
    } else {
      setContributors(data); 
      setError({message: ""});
    }

    setIsLoading(false);
  }

  return (
    <div className="details-outer-body">
      <div className="details-body">
        <div className="details-top-section">
          <h1 className="details-repo-header"><i className="fa-brands fa-github"></i>{details.name}</h1>
        </div>
        <div className="details-main-section">
          <div className="details-left-section">
            <div className="label-value-pair-container">
              <b className="details-label details-label1">Repository:</b>
              <a href={details.html_url} rel="noopener noreferrer" target="_blank" className="details-value1">{details.name}</a>
              <b className="details-label details-label2">Repo Descr:</b>
              <span className="details-value2">{details.description}</span>
              <b className="details-label details-label3">Created On:</b>
              <span className="details-value3">{(new Date([...details.created_at].slice().join(""))).toString()}</span>
            </div>
            <div className="contributors-container ">
              <div className="contributors-label details-label"><b>Contributors <span className="badge badge-contributors">{contributors.length}</span></b></div>
              <div className="contributors">
                {(error.message !== "") ? <Error message={error.message}/> : null}
                {(isLoading) ? <Spinner /> : null}
                {
                  contributors.map( contributor => {
                    return (
                      <div key={contributor.id}>
                        <a href={contributor.html_url} rel="noopener noreferrer" target="_blank">
                          <img src={contributor.avatar_url} className="contributors-imgs" alt={"Contributor " + contributor.login + "'s " + "avatar"}/>
                        </a>
                      </div>
                    );
                  })
                }
              </div>
            </div>
          </div>
          <div className="details-right-section">
            <div className="details-counters">
              <div className="details-label">
                <div>
                  <FontAwesomeIcon icon="fa-solid fa-code-fork" color='#F99F38'/> Forks
                </div>
                <span className="badge badge-fork">{details.forks_count}</span>
              </div>
              <div className="details-label">
                <div>
                  <FontAwesomeIcon icon="fa-solid fa-eye" color='#315399'/> Watch
                </div>
                <span className="badge badge-watch">{details.watchers_count}</span>
              </div>
              <div className="details-label">
                <div>
                  <FontAwesomeIcon icon="fa-solid fa-star" color="#F7DE3A"/> Stars
                </div>
                <span className="badge badge-stars">{details.stargazers_count}</span> 
              </div>
            </div>
              <img src={details.owner.avatar_url} alt="Owner avatar" className="owner-avatar"/>
              <div className="owner-details">
                <b className="details-label">Owner </b>
                <a href={details.owner.html_url} rel="noopener noreferrer" target="_blank">{details.owner.login}</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ResultDetails;
