      }

      setItemsError({message: ""});

    }
  }



  return ( 
    <>
    <Nav/>
    {(itemsError.message !== "") ? <Error message={itemsError.message}/> : null}
    {(itemsAreLoading) ? <Spinner /> : null}

    <Footer/>
    </>
  );
}

export default Bookmark;
