import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import SearchForm from "./SearchForm.js";
import Loading from "./"
import "./index.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSpinner, faCircleNotch } from '@fortawesome/free-solid-svg-icons';
import '@fortawesome/fontawesome-free/js/all';

const Results = (props) => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const params = useParams();

  useEffect(() => {
    fetchData().catch( (error) => {
      console.log(error);
    })
  }, [params.query]);

  useEffect(() => {
    if (!isLoading){
      //remove the loading screen
    }
  }, [isLoading]);

  const fetchData = async () => {
    const results = await fetch("https://api.github.com/search/repositories?q=" + params.query);
    const data = await results.json();

    setData(data);
    setIsLoading(false);
  };

  function SearchResults(props) {
    if (props.totalCount !== undefined) {
      return (
        <div className="search-results">
          <h3 className="results-header">Search found {props.totalCount} results</h3>
          <ul className="results-list">
            {
              data.items.map( item => {
                return (
                  <li key={item.id}>
                    <div className="result-container">
                      <div className="repo-container">
                        <div className="results-label results-repo">
                          <b>Repository:</b>
                          <Link to={`${item.id}`} state={{item_details: item}}>{item.name}</Link>
                        </div> 
                        <div className="results-label results-owner">
                          <b>Owner:</b> 
                          {item.owner.login}
                        </div>
                        <div className="results-label results-descr">
                          <b>Description:</b>
                          {item.description}
                        </div>
                      </div>
                      <img src={item.owner.avatar_url} alt="repo owner avatar" className="avatar"/>
                    </div>
                  </li>
                );
              })
            }
          </ul>
        </div>
      );
    }

    return null;
  }

  return (
    <div>
      <SearchForm style_top="true" />
      {/* <Loading loading={isLoading} /> */}
      <div className="spinner">
        <svg width="38" height="38" viewBox="0 0 38 38" xmlns="http://www.w3.org/2000/svg" stroke="white">
            <g fill="white" fill-rule="evenodd">
                <g transform="translate(1 1)" stroke-width="2">
                    <circle stroke-opacity=".5" cx="18" cy="18" r="18"/>
                    <path d="M36 18c0-9.94-8.06-18-18-18">
                        <animateTransform
                            attributeName="transform"
                            type="rotate"
                            from="0 18 18"
                            to="360 18 18"
                            dur="1s"
                            repeatCount="indefinite"/>
                    </path>
                </g>
            </g>
        </svg>
        <FontAwesomeIcon ><i class="fa-solid fa-loader"/></FontAwesomeIcon>
        <FontAwesomeIcon icon={faSpinner} color="blue" spin/>
        <FontAwesomeIcon icon={faCircleNotch} color="red" spin/>
      </div>
      <SearchResults totalCount={data.total_count} query={params.query} />
    </div>
  )
}

export default Results;
