import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import SearchForm from "./SearchForm.js";
import Spinner from "./Spinner.js"
import "./index.css";

const Results = (props) => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const params = useParams();

  useEffect(() => {
    fetchData().catch( (error) => {
      console.log(error);
    })
  }, [params.query]);

  const fetchData = async () => {
    const results = await fetch("https://api.github.com/search/repositories?q=" + params.query);
    const data = await results.json();

    setData(data);
    setIsLoading(false);
  };

  function SearchResults(props) {
    if (props.totalCount !== undefined) {
      return (
        <div className="search-results">
          <h3 className="results-header">Search found {props.totalCount} results</h3>
          <ul className="results-list">
            {
              data.items.map( item => {
                return (
                  <li key={item.id}>
                    <div className="result-container">
                      <div className="repo-container">
                        <div className="results-label results-repo">
                          <b>Repository:</b>
                          <Link to={`${item.id}`} state={{item_details: item}}>{item.name}</Link>
                        </div> 
                        <div className="results-label results-owner">
                          <b>Owner:</b> 
                          {item.owner.login}
                        </div>
                        <div className="results-label results-descr">
                          <b>Description:</b>
                          {item.description}
                        </div>
                      </div>
                      <img src={item.owner.avatar_url} alt="repo owner avatar" className="avatar"/>
                    </div>
                  </li>
                );
              })
            }
          </ul>
        </div>
      );
    }

    return null;
  }

  return (
    <div>
      <SearchForm style_top="true" />
      {(isLoading) ? <Spinner /> : null}
      <SearchResults totalCount={data.total_count} query={params.query} />
    </div>
  );
}

export default Results;
